def g3n3r4t3_p01nt3r_m0v3m3nts(x,y):
    
    m0v3m3nts = ""
    
    for i in range(8): 
        m0v3m3nts += "."
        if i == 0:
            m0v3m3nts += "-" * (x)
        elif i == 1:
            m0v3m3nts += "+" * (y)
        elif i == 2:
            m0v3m3nts += "-" * (x*x)
        elif i == 3:
            m0v3m3nts += "+" * (y + 2 * x)
        elif i == 4:
            m0v3m3nts += "-" * (x+y)
        elif i == 5:
            m0v3m3nts += "+" * (y*2 - 1)
        elif i == 6:
            m0v3m3nts += "-" * (x*x*x-2)
        elif i == 7:
            m0v3m3nts += "+" * (y + 5)
    
    return m0v3m3nts + "."

def cr34t3_c0mpl3x_p4tt3rn(indexes):
    if indexes == [8]:
        return ".++++++"
    
    p4tt3rns = [
        "-.---.>---",
        "+++++.",
        ".--------------- ", 
        "------------",
        ".++++++++++++++ ",  
        "--------.",
        ".+ ",  
        "<-----",
        "-.-------",
        ".+++++++++ "
    ]
    
    p4tt3rn = ""
    for i in indexes:
        p4tt3rn += p4tt3rns[i]
    
    return p4tt3rn



def th1nk(x,y,indexes):
    p4rt_1 = "+" * 10 +"[>+>+++>+++++++>++++++++++<<<<-]" + 4*">"
    p4rt_2 = g3n3r4t3_p01nt3r_m0v3m3nts(x,y)
    p4rt_3 = cr34t3_c0mpl3x_p4tt3rn(indexes)
    p4rt_4 = "<++.+.>."
    p4rt_5 = "+++++++++++++++++++++++++."
    parts = [p4rt_1, p4rt_2, p4rt_3, p4rt_4, p4rt_5]
    f1n4l_0utput = ''.join(parts)
    
    return f1n4l_0utput

